var util = require('util')
  	, Buffer = require('buffer').Buffer
  	, events = require('events')
	, ZLibStream = require('zlibstream').ZLibStream
	, dictionary = require('./SMCompressionDictionary');

function ProcessCompression(){
	var self = this;
	var realEmit = this.emit;
 	var zstream = new ZLibStream();
		zstream.setInflateDictionary(dictionary);
		zstream.setDeflateDictionary(dictionary);

	self.processType = {
		inbound : 0,
		outbound: 1
	};

	this.on('compression', function(data){
		var compressHeaders = zstream.deflate(data);
		
		self.emit('processFinished', compressHeaders);
	});

	this.on('decompression', function(data){
		var decompressHeaders;
		try{
			decompressHeaders = zstream.inflate(data);
		} catch(e){
			console.log('err: ' + e);
		}

		self.emit('processFinished', decompressHeaders);
	});

	self.process = function (bufheaders, type, options, flags){
		if (options && !options.compressionIsStateful){ 
			zstream.resetDeflate();
			zstream.resetInflate();
			zstream.setInflateDictionary(dictionary);
			zstream.setDeflateDictionary(dictionary);
		}
		if (flags != 0x04 && flags != 0x02)
			type == self.processType.inbound ? self.emit('decompression', bufheaders) : self.emit('compression', bufheaders);
		else
			self.emit('processFinished', bufheaders);
	};

	return self;
}
util.inherits(ProcessCompression, events.EventEmitter);
module.exports = ProcessCompression;